sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    current.name = gs.getMessage("{0} - {1} {2}", [getDayOfMonth(current.start_date), getDayOfMonth(current.end_date), getMonth(current.start_date)]);

    function getDayOfMonth(date) {
        var gd = new GlideDate();
        gd.setValue(date);
        return gd.getDayOfMonthNoTZ();
    }

    function getMonth(date) {
		var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        var gd = new GlideDate();
        gd.setValue(date);
        return months[gd.getMonthNoTZ()-1];
    }



})(current, previous);
