function matchIngredient(recipe, instructions) {
    var grX_62500MI = new GlideRecord('x_62500_mealplan_ingredient');
    grX_62500MI.addEncodedQuery("");
    grX_62500MI.orderBy('name');
    //grX_62500MI.setLimit(100);
    grX_62500MI.query();
    var ingredient = "";
    while (grX_62500MI.next()) {
        ingredient = grX_62500MI.getValue("name").toUpperCase();
        // if (instructions.match((new RegExp(ingredient,"i")))) {
        //     gs.info("Ingredient match: " + ingredient);
        //     //  insertRecipeIngredient(grX_62500MI.getValue("sys_id"), recipe);
        // }

        if (instructions.toUpperCase().indexOf(ingredient)>-1) {
            insertRecipeIngredient(grX_62500MI.getValue("sys_id"), recipe);
        }
    }
}

function insertRecipeIngredient(ingredient, recipe) {
    var grX_62500MM2RI = new GlideRecord('x_62500_mealplan_m2m_recipe_ingredient');
    grX_62500MM2RI.initialize();
    grX_62500MM2RI.x_62500_mealplan_ingredient = ingredient;
    grX_62500MM2RI.x_62500_mealplan_recipe = recipe;
    grX_62500MM2RI.insert();
}
